mapper.select:

select rownum, emp_postNo, emp_duty, emp_stime, emp_etime, emp_workPlace, emp_type, emp_startDate, emp_endDate, emp_title, emp_content 
from (
    select rownum, emp_postNo, emp_duty, emp_stime, emp_etime, emp_workPlace, emp_type, emp_startDate, emp_endDate, emp_title, emp_content 
    from (
        select row_number() over(order by emp_postNo desc) as rownum,
               emp_postNo, emp_duty, emp_stime, emp_etime, emp_workPlace, emp_type, emp_startDate, emp_endDate, emp_title, emp_content 
        from tbl_empPost



        order by emp_postNo desc
    ) a
    where rownum <= (#{pageNum} * #{amount})
) aa
where rownum > (#{pageNum} - 1) * #{amount}
